PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE [dishes] ("name" text DEFAULT ' ',"time" text DEFAULT ' ',"portions" text DEFAULT ' ',"ingedients" text DEFAULT ' ',"recipe" text DEFAULT ' ', "type" TEXT, "category" TEXT, "nutritionFacts" TEXT, "alreadyTaken" TEXT, meal text);
